import React from 'react';
import ProfileData from '../data/schoolProfiles.json';


function SchoolProfile({match}) {
  //console.log(match.params.name);
  const school = ProfileData.map((school)=>{
    if (school.name === match.params.name){
      return <>
          <img src={school.schoolLogoUrl} className="card-img-top mx-auto" alt="..." style={{width: 150 + 'px', height: 150 + 'px'}}></img>
            <div className="card-body">
              <h1 className="card-title">{school.name}</h1>
              <p className="card-text">{school.address}</p>
            </div>
      </> 
    }
  })

  const programSelector = ProfileData.map((school, index) => {
    let schoolProgramArr = [];
    if (school.name === match.params.name){
      let schoolDetails = Object.keys(school);
      let detailValues = Object.values(school);
      detailValues.map((program, keysIndex) => {
        if (program === 'TRUE'){
          schoolProgramArr.push(schoolDetails[keysIndex]);
        }
      })
    }
    //console.log(outArr);
    return schoolProgramArr;
  })

  const programCards = programSelector.map((programs, index) => {
    for (program in programs){
      return <>
            <div class="row">
              <div class="col-sm-6">
                <div class="card"></div>
                  <div class="card" style={{width: 18 + 'rem'}}>
                    <img src="..." class="card-img-top" alt="..."></img>
                    <div class="card-body">
                      <h5 class="card-title">{program[index]}</h5>
                      <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                      <a href="#" class="btn btn-primary">Go somewhere</a>
                    </div>
                  </div>
                </div>
              </div>
            </>
    }
  })



  return (
    <div className="card mb-3">
        {school}
        {programCards}
    </div>
  );
};

export default SchoolProfile;
