import React, {useState, useEffect} from 'react';
import ProfileData from '../data/schoolProfiles.json';

function SchoolList() {
  const {google} = require('googleapis');
  const keys = require('./keys.json');
  const fs = require('fs');

    // Creates a web token (JWT - JSON Web Token)
  const client = new google.auth.JWT(
    keys.client_email,
    null,
    keys.private_key,
    ['https://www.googleapis.com/auth/spreadsheets'] // scope (read/write access)
  );

  // uses token created above (client) to connect to the sheet and run functions (gsrun)
  client.authorize((err,tokens) => {
    if (err) {
      console.log(err);
      return;
    } else {
      console.log('Connected...');
      gsrun(client);
    }
  });
  // read/write data from sheet. requires the client token (cl)
  async function gsrun(cl) {
    const gsapi = google.sheets({version: 'v4', auth: cl}); // define API verison and auth info (from above)
    const opt = {
      spreadsheetId: '1jXrk6uXX580gHV1TZAHoWrH_UzkJnkLE3TdAGR4qD0s',
      range: 'schoolProfiles!A1:AV11'
    };

    let res = await gsapi.spreadsheets.values.get(opt);
    let values = res.data.values;
    console.log(values);
  }




  return (
    <div>
      <h1 style ={{textAlign: "center", margin: 20 + 'px'}}>School Profiles</h1>
        <div class="row">
          {ProfileData.map((schoolProfile, index)=>{
            return <>
              <div class="col-sm">
                <div class="card" style={{width: 18+ 'rem', height: 10 + 'rem'}}>
                  <div class="card-body">
                    <h5 class="card-title">{schoolProfile.name}</h5>
                    <h6 class="card-subtitle mb-2 text-muted">{schoolProfile.address}</h6>
                    <a href="#" class="btn btn-primary">Generate page</a>
                  </div>
                </div>
              </div>
            </>
          })}
      </div>
    </div>
  );
}

export default SchoolList;


